mapper.select:
select sum(order_quan) as prdStock
from noel_order
where prd_no = #{prdNo}

select
count(*)
from noel_order no
where (select marketer_id
from noel_product np where no.prd_no = np.prd_no)
=#{marketerId}

mapper.update:
update noel_order no
set order_status =#{orderStatus}
where order_no =#{orderNO}
